function enableTabClick(node) {
  node.setAttribute('tabindex', '0');
  node.addEventListener('keydown', function (e) {
    var key = e.key,
        which = e.which,
        keyCode = e.keyCode;

    if (key === 'Enter' || which === 13 || keyCode === 13) {
      e.preventDefault();
      if (typeof e.target.click === 'function') {
        e.target.click();
      }
    }
  });
}

export { enableTabClick };